:root {
  --radius: 0.75rem;

  /* Base palette – warm off-white background */
  --background: oklch(0.978 0.005 80);
  --foreground: oklch(0.18 0.02 260);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.02 260);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.02 260);

  /* Primary – rich deep navy */
  --primary: oklch(0.28 0.09 258);
  --primary-foreground: oklch(0.98 0 0);

  /* Secondary – light cool gray */
  --secondary: oklch(0.95 0.004 260);
  --secondary-foreground: oklch(0.28 0.09 258);

  /* Muted */
  --muted: oklch(0.95 0.004 260);
  --muted-foreground: oklch(0.52 0.02 260);

  /* Accent – warm amber */
  --accent: oklch(0.88 0.12 68);
  --accent-foreground: oklch(0.25 0.05 60);

  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.91 0.006 260);
  --input: oklch(0.94 0.004 260);
  --ring: oklch(0.55 0.09 258);

  /* Charts */
  --chart-1: oklch(0.55 0.18 258);
  --chart-2: oklch(0.72 0.14 68);
  --chart-3: oklch(0.62 0.15 162);
  --chart-4: oklch(0.65 0.18 310);
  --chart-5: oklch(0.60 0.15 25);

  /* Sidebar – deep navy */
  --sidebar: oklch(0.22 0.07 258);
  --sidebar-foreground: oklch(0.96 0.005 260);
  --sidebar-primary: oklch(0.78 0.12 68);
  --sidebar-primary-foreground: oklch(0.18 0.04 60);
  --sidebar-accent: oklch(0.30 0.07 258);
  --sidebar-accent-foreground: oklch(0.96 0.005 260);
  --sidebar-border: oklch(0.30 0.05 258);
  --sidebar-ring: oklch(0.55 0.09 258);

  /* Custom design tokens */
  --font-sans: 'Plus Jakarta Sans', sans-serif;
  --gradient-hero: linear-gradient(135deg, oklch(0.28 0.09 258), oklch(0.35 0.12 280));
  --gradient-amber: linear-gradient(135deg, oklch(0.78 0.16 68), oklch(0.72 0.14 55));
  --gradient-card: linear-gradient(160deg, oklch(1 0 0), oklch(0.97 0.006 260));
  --shadow-card: 0 2px 16px -4px oklch(0.18 0.09 258 / 0.12);
  --shadow-hero: 0 8px 40px -8px oklch(0.28 0.09 258 / 0.35);
  --shadow-amber: 0 4px 20px -4px oklch(0.72 0.14 68 / 0.4);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);

