import { useEffect, useState } from 'react';
import { StatCard } from '@/components/StatCard';
import { LivingAppsService } from '@/services/livingAppsService';
import type { Dozenten, Raeume, Teilnehmer, Kurse, Anmeldungen } from '@/types/app';

export default function DashboardOverview() {
  const [dozentenCount, setDozentenCount] = useState(0);
  const [raeumeCount, setRaeumeCount] = useState(0);
  const [teilnehmerCount, setTeilnehmerCount] = useState(0);
  const [kurseCount, setKurseCount] = useState(0);
  const [anmeldungenCount, setAnmeldungenCount] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadStats() {
      try {
        const [dozentenData, raeumeData, teilnehmerData, kurseData, anmeldungenData] = await Promise.all([
          LivingAppsService.getDozenten(),
          LivingAppsService.getRaeume(),
          LivingAppsService.getTeilnehmer(),
          LivingAppsService.getKurse(),
          LivingAppsService.getAnmeldungen(),
        ]);
        setDozentenCount(dozentenData.length);
        setRaeumeCount(raeumeData.length);
        setTeilnehmerCount(teilnehmerData.length);
        setKurseCount(kurseData.length);
        setAnmeldungenCount(anmeldungenData.length);
      } catch (e) {
        console.error('Failed to load stats:', e);
      } finally {
        setLoading(false);
      }
    }
    loadStats();
  }, []);

  return (
    <div className="space-y-8">
      {/* === HERO SECTION — Customize with gradient, welcome text, key metrics === */}
      <div>
        <h1 className="text-3xl font-bold tracking-tight">Willkommen</h1>
        <p className="text-muted-foreground mt-1">Hier ist eine Übersicht Ihrer Daten.</p>
      </div>

      {/* === KPI CARDS — Customize with icons, colors, descriptions === */}
      <div className="grid grid-cols-2 lg:grid-cols-5 gap-4">
        <StatCard
          title="Dozenten"
          value={loading ? '...' : dozentenCount}
          description="Dozenten im System"
        />
        <StatCard
          title="Räume"
          value={loading ? '...' : raeumeCount}
          description="Räume im System"
        />
        <StatCard
          title="Teilnehmer"
          value={loading ? '...' : teilnehmerCount}
          description="Teilnehmer im System"
        />
        <StatCard
          title="Kurse"
          value={loading ? '...' : kurseCount}
          description="Kurse im System"
        />
        <StatCard
          title="Anmeldungen"
          value={loading ? '...' : anmeldungenCount}
          description="Anmeldungen im System"
        />
      </div>

      {/* === CHARTS / CONTENT — Build your dashboard here === */}
      {/* Ideas: recharts BarChart/LineChart, upcoming items list, recent activity feed */}
    </div>
  );
}

